import { Component, inject } from '@angular/core';
import {MatFormFieldModule} from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input'
import {MatIconModule} from '@angular/material/icon';
import {MatButtonModule} from '@angular/material/button';
import {MatDividerModule} from '@angular/material/divider';
import { FormBuilder, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';


@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.scss'],
  standalone: true,
  imports: [MatFormFieldModule, MatInputModule, MatIconModule, MatButtonModule, MatDividerModule, FormsModule, ReactiveFormsModule]
})
export class AuthComponent {
  readonly fb = inject(FormBuilder);

  isShowPassword = false;
  isSignIn = true;

  authFormGroup: FormGroup;

  constructor () {
    this.authFormGroup = this.fb.group({
      username: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  toggleShowPassword(event: MouseEvent): void {
    this.isShowPassword = !this.isShowPassword;
    event.stopPropagation();
  }

  handleSignUp(): void {
    this.isSignIn = !this.isSignIn;
    this.authFormGroup.reset();
    this.authFormGroup.markAsUntouched();
  }

  onSubmit(): void {
    console.log(this.authFormGroup.value)
  }
}
<div class="auth-page">
  <div class="auth-page__container auth-page__container--left">
    <img class="brand__logo" src="/assets/images/logo-brand.svg" alt="logo brand here" />
  </div>
  <div class="auth-page__container auth-page__container--right">
    <form class="auth-page__form" [formGroup]="authFormGroup" (ngSubmit)="onSubmit()">
      <div class="auth-page__logo">
        <img class="header__logo" src="/assets/images/logo-header.svg" alt="logo header here" />
      </div>
      <div class="auth-page__header">
        <h1>{{ isSignIn ? 'Sign In' : 'Create an account' }}</h1>
      </div>
      <div class="auth-page__fields">
        <mat-form-field appearance="outline" floatLabel="always">
          <mat-label>Username</mat-label>
          <input matInput type="text" formControlName="username">
        </mat-form-field>
        <mat-form-field appearance="outline" floatLabel="always">
          <mat-label>Password</mat-label>
          <input matInput [type]="isShowPassword ? 'text' : 'password'" formControlName="password" />
          <button
            type="button"
            mat-icon-button
            matSuffix
            (click)="toggleShowPassword($event)"
            [attr.aria-label]="'Hide password'"
          >
            <mat-icon>{{isShowPassword ? 'visibility' : 'visibility_off'}}</mat-icon>
          </button>
        </mat-form-field>
        <!-- @if(!isSignIn) { -->
          <ul class="auth-page__password-requirements">
            <li>Cannot contain your name or email address</li>
            <li>Atleast 8 characters</li>
            <li>Contains a number or symbol</li>
          </ul>
        <!-- } -->
      </div>
      <button type="submit" mat-flat-button color="primary" [disabled]="authFormGroup.invalid">{{ isSignIn ? 'Sign in' : 'Submit' }}</button>
      <div class="auth-page__no-account mat-h2">
        <div>{{ isSignIn ? "Don't" : 'Already' }} have an account?</div>
        <div class="primary-color" role="button" (click)="handleSignUp()"> {{ isSignIn ? 'Sign up' : 'Sign in' }}</div>
      </div>
      <div class="custom-divider">
        <mat-divider></mat-divider>
        <span>OR</span>
        <mat-divider></mat-divider>
      </div>
      <div class="auth-page__alt-auth">
        <button type="button"mat-stroked-button>
          <mat-icon svgIcon="google"></mat-icon>
          Continue with Google
        </button>
        <button type="button"mat-stroked-button>
          <mat-icon svgIcon="facebook"></mat-icon>
          Continue with Facebook
        </button>
      </div>
    </form>
  </div>
</div>
