import { Component } from '@angular/core';

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.scss'],
  standalone: true
})
export class AuthComponent {

}
<div class="auth-page">
  <div class="auth-page__container auth-page__container--left">
    <img class="brand__logo" src="/assets/images/logo-brand.svg" alt="logo brand here" />
  </div>
  <div class="auth-page__container auth-page__container--right"></div>
</div>
